mapper.update:
update shop
shop_name = #{shopName}, area_id = #{areaId}, shop_category_id = #{shopCategoryId}, shop_description = #{shopDescription}, shop_address = #{shopAddress}, phone = #{phone}, shop_img = #{shopImg}, priority = #{priority}, last_edit_time = #{lastEditTime}, enable_status = #{enableStatus}, advice = #{advice}
where shop_id = #{shopId};

mapper.select:
select shop_img
from shop
where shop_id = #{shopId};

select shop_id, owner_id, area_id, shop_category_id, shop_name, shop_description, shop_address, phone, shop_img, priority, create_time, last_edit_time, enable_status, advice from shop
and shop_category_id = #{shop.shopCategoryId} and area_id = #{shop.areaId} and shop_name like '%${shop.shopName}%' and owner_id = #{shop.ownerId} and enable_status =1
order by priority desc ,enable_status desc limit #{rowIndex},#{pageSize};

select count(1) as number
from shop

    and shop_category_id = #{shop.shopCategoryId}
    and area_id = #{shop.areaId}
    and shop_name like '%${shop.shopName}%'
    and enable_status = #{shop.enableStatus}
    and owner_id = #{shop.ownerId}
